import React from "react";
import "./Expenses.css";

import ExpenseItem from "./ExpenseItem";
import WrapperStd from "../Wrapper/WrapperStd";
import ExpensesFilter from "./ExpensesFilter";

export default (props) => {
  const [filteredYear, setFilteredYear] = React.useState("2023");

  const filterList = props.expensesList.filter((exp) => exp.year === filteredYear);

  function onFilterChangeHandler(selectedYear) {
    console.log(selectedYear);
    setFilteredYear(selectedYear);
  }

  return (
    <WrapperStd className="expenses-list">
      <ExpensesFilter
        selected={filteredYear}
        onFilterChange={onFilterChangeHandler}
      />
      {props.filterList.map((expense) => (
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />
      ))}
    </WrapperStd>
  );
};
