import React from "react";
import "./Expenses.css";

import ExpenseItem from "./ExpenseItem";
import WrapperStd from "../Wrapper/WrapperStd";
import ExpensesFilter from "./ExpensesFilter";

export default (props) => {
  const [expenses, setExpanses] = React.useState(props.expensesList);
  const [filteredYear, setFilteredYear] = React.useState("2023");
  let comm = "2021,2022,2024";

  if (filteredYear === "2021") comm = "2022,2023,2024";
  else if (filteredYear === "2022") comm = "2021,2023,2024";
  else if (filteredYear === "2024") comm = "2021,2022,2023";

  function onFilterChangeHandler(selectedYear) {
    console.log(selectedYear);
    setFilteredYear(selectedYear);
  }

  return (
    <WrapperStd className="expenses-list">
      <ExpensesFilter
        selected={filteredYear}
        onFilterChange={onFilterChangeHandler}
      />
      <p>{comm}</p>
      <ExpenseItem
        title={expenses[0].title}
        amount={expenses[0].amount}
        date={expenses[0].date}
      ></ExpenseItem>
      <ExpenseItem
        title={expenses[1].title}
        amount={expenses[1].amount}
        date={expenses[1].date}
      ></ExpenseItem>
      <ExpenseItem
        title={expenses[2].title}
        amount={expenses[2].amount}
        date={expenses[2].date}
      ></ExpenseItem>
      <ExpenseItem
        title={expenses[3].title}
        amount={expenses[3].amount}
        date={expenses[3].date}
      ></ExpenseItem>
    </WrapperStd>
  );
};
