import React from "react";

import Expenses from "./components/Expenses/Expenses";
import NewExpense from "./components/NewExpense/NewExpense";

function App() {
  const [expenses_list, setExpensesList] = React.useState([
    {
      id: "e1",
      title: "Ice cream in Magnolia",
      amount: 15.00,
      date: new Date(2023, 6, 20),
    },
    { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
    {
      id: "e3",
      title: "Car Insurance",
      amount: 294.67,
      date: new Date(2021, 2, 28),
    },
    {
      id: "e4",
      title: "New Desk (Wooden)",
      amount: 450,
      date: new Date(2021, 5, 12),
    },
  ]);

  function onSaveNewExpenseHandler(newExpenseData) {

    setExpensesList((prevState) => {
      return expenses_list.push(newExpenseData)
    });
  }

  return (
    <div>
      <NewExpense onSaveNewExpense={onSaveNewExpenseHandler} />
      <Expenses expenses_list={expenses_list}/>
    </div>
  );
}

export default App;
